    <code class="language-php"><div class=highlight><pre><span></span><span class=cp>&lt;?php</span>

    <span class=nv>$x</span> <span class=o>=</span> <span class=mi>10</span><span class=p>;</span>  
    <span class=nv>$y</span> <span class=o>=</span> <span class=s2>&quot;10&quot;</span><span class=p>;</span>

    <span class=nb>var_dump</span><span class=p>(</span><span class=nv>$x</span> <span class=o>===</span> <span class=nv>$y</span><span class=p>);</span> 
<span class=cp>?&gt;</span>
</pre></div></code>
