        <span class=k>for</span> <span class=p>(</span><span class=nv>$j</span> <span class=o>=</span> <span class=mi>0</span><span class=p>;</span> <span class=nv>$j</span> <span class=o>&lt;</span> <span class=nv>$n</span> <span class=o>-</span> <span class=nv>$i</span> <span class=o>-</span> <span class=mi>1</span><span class=p>;</span> <span class=nv>$j</span><span class=o>++</span><span class=p>)</span> <span class=p>{</span>
            <span class=k>if</span> <span class=p>(</span><span class=nv>$arr</span><span class=p>[</span><span class=nv>$j</span><span class=p>]</span> <span class=o>&gt;</span> <span class=nv>$arr</span><span class=p>[</span><span class=nv>$j</span> <span class=o>+</span> <span class=mi>1</span><span class=p>])</span> <span class=p>{</span>
                <span class=k>list</span><span class=p>(</span><span class=nv>$arr</span><span class=p>[</span><span class=nv>$j</span><span class=p>],</span> <span class=nv>$arr</span><span class=p>[</span><span class=nv>$j</span> <span class=o>+</span> <span class=mi>1</span><span class=p>])</span> <span class=o>=</span> <span class=p>[</span><span class=nv>$arr</span><span class=p>[</span><span class=nv>$j</span> <span class=o>+</span> <span class=mi>1</span><span class=p>],</span> <span class=nv>$arr</span><span class=p>[</span><span class=nv>$j</span><span class=p>]];</span>
            <span class=p>}</span>
        <span class=p>}</span>
    <span class=p>}</span>
